import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet } from "react-native";
import { Button } from "../../components/Button";

export function HomeScreen({ navigation }) {
    return (
        <View style={styles.container}>
            <Text>Hooks</Text>
            <Button 
                text="useState" 
                onPress={() => {navigation.navigate("UseState")}}
            />  
            <Button
                text="useEffect"
                onPress={() => {navigation.navigate("UseEffect")}}
            />
            <Button
                text="useRef"
                onPress={() => {navigation.navigate("UseRef")}}
            />
            <Button
                text="useContext"
                onPress={() => {navigation.navigate("UseContext")}}
            />          
            <Button
                text="useReducer"
                onPress={() => {navigation.navigate("UseReducer")}}
            />
            <Button
                text="useCallback"
                onPress={() => {navigation.navigate("UseCallback")}}
            />
            <Button
                text="useMemo"
                onPress={() => {navigation.navigate("UseMemo")}}
            />          
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#ddd',    
        justifyContent: 'center',
        padding: 20,        
    },    
});



function Exemplo() {
  const [count, setCount] = useState(0);

  // Similar ao componentDidMount e componentDidUpdate:  useEffect(() => {    // Atualiza o titulo do documento usando a API do browser    document.title = `Você clicou ${count} vezes`;  });
  return (
    <div>
      <p>Você clicou {count} vezes</p>
      <button onClick={() => setCount(count + 1)}>
        Clique aqui
      </button>
    </div>
  );
}
