import React, { useEffect, useState } from "react";
import { Button } from "react-native-paper";


export default function UseEffectExample() {
    const [counter, setCounter] = useState(0);



    useEffect(() => {
        document.title = 5; //CÃ“DIGO QUE A GENTE QUER EXECUTAR
        return () => document.title = 'React App'; // CASO O COMPONENTE SEJA DSMONTADO O QUE EU PRECISO FAZER
    }, [counter]);  //QUANDO A GENTE QUER EXECUTAR. EM BRANCO ELE EXECUTA APENAS UMA VEZ                           



    /*useEffect(()=> {
             document.title = counter;   
    }, [counter]);    
    */

    return (
        <>
            <h1>{counter}</h1>

            <button onClick={() => setCounter(counter + 1)}>Adicionar</button>
        </>
    )
}
