import React, { useEffect, useState } from "react";
import { Button } from "react-native-paper";


export default function UseEffectExample() {
    const [counter, setCounter] = useState(0);
    


    useEffect(()=> {
             document.title = counter;         //CÃ“DIGO QUE A GENTE QUER QUE EXECUTE
    }, [counter]);    
    
    //SE COLOCAR UMA VARIAVEL DENTRO DO ARRAY ELE VAI EXECUTAR TODA VEZ QUE A VARIAVEL ALTERAR. EM BRANCO ELE EXECUTA APENAS UMA VEZ
    return (
        <>
        <h1>{counter}</h1>

        <button onClick={() => setCounter(counter + 1)}>Adicionar</button>
        </>
    )
}
